package com.rajeshdonepudi.myapplication;

import android.content.DialogInterface;
import android.content.Intent;
import android.os.Bundle;

import androidx.appcompat.app.AlertDialog;
import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;


import java.util.ArrayList;

public class MainActivity extends AppCompatActivity implements RecyclerViewAdapter.onItemClickListener {
    public static final String IMAGE = "image";
    public static final String TEXT = "text";
    private ArrayList<String> imageArrayList = new ArrayList<>();
    private ArrayList<String> textArrayList = new ArrayList<>();

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);
        loadImages();
    }

    public void loadImages() {
        imageArrayList.add("https://www.behindwoods.com/tamil-actress/samantha-ruth-prabhu/samantha-ruth-prabhu-stills-photos-pictures-2008.jpg");
        textArrayList.add("Canada");

        imageArrayList.add("data:image/jpeg;base64,");
        textArrayList.add("Michigan");

        imageArrayList.add("https://data1.ibtimes.co.in/cache-img-0-450/en/full/694711/1548902525_samantha-akkineni.jpg");
        textArrayList.add("Simla");

        imageArrayList.add("https://akm-img-a-in.tosshub.com/indiatoday/images/story/201904/Samantha.jpeg?k0Zb7c_anRzJjKFVy4_lVDPEGXJ3S2NS");
        textArrayList.add("Watson");

        imageArrayList.add("https://i.pinimg.com/originals/6c/23/57/6c23577779d3955333758eb6cb6a0c09.jpg");
        textArrayList.add("Hello");

        imageArrayList.add("http://assets-news-bcdn-ll.dailyhunt.in/cmd/resize/400x400_60/fetchdata15/images/42/db/8d/42db8d0f780fb7e270375f6e09c1eaf4.jpg");
        textArrayList.add("Mike");

        imageArrayList.add("https://akm-img-a-in.tosshub.com/indiatoday/images/story/201804/samantha-naga.jpeg?IhJ5UCLlN6sa2bvq3JgAdGelfaXKZI4w");
        textArrayList.add("Miss you");
        initRecyclerView();

    }

    public void initRecyclerView() {
        RecyclerView recyclerView = (RecyclerView) findViewById(R.id.recyclerView);
        RecyclerViewAdapter recyclerViewAdapter = new RecyclerViewAdapter(this, imageArrayList, textArrayList);
        recyclerView.setAdapter(recyclerViewAdapter);
        recyclerView.setLayoutManager(new LinearLayoutManager(this));
        recyclerViewAdapter.setOnItemClickListener(MainActivity.this);
    }

    @Override
    public void onItemClick(int position) {
        Intent intent = new Intent(MainActivity.this, SecondActivity.class);
        intent.putExtra(TEXT, imageArrayList.get(position));
        intent.putExtra(IMAGE, textArrayList.get(position));
        startActivity(intent);
    }
    @Override
    public void onBackPressed() {
        new AlertDialog.Builder(this)
                .setIcon(R.drawable.baseline_warning_black_36)
                .setTitle("Exit")
                .setMessage("Are you sure you want to Exit?")
                .setPositiveButton("Yes", new DialogInterface.OnClickListener()
                {
                    @Override
                    public void onClick(DialogInterface dialog, int which) {
                        finish();
                    }

                })
                .setNegativeButton("No", null)
                .show();
    }
}
